import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    token: '', // 保存token字符串
    userInfo: {}// 用来保存用户信息
  },
  getters: {
  },
  mutations: {
    // 保存token
    updataToken (state, val) {
      state.token = val
    }
  },
  actions: {
  },
  modules: {
  }
})
