// 封装具体的接口请求方法
// 注意：每个方法只负责请求一个url地址
import request from '@/utils/request.js'
// import store from '@/store'
// 导出接口，为了在逻辑页面引入后调用
export const registerAPI = ({ username, password, repassword }) => {
  return request({
    url: '/api/reg',
    method: 'POST',
    data: {
      username,
      password,
      repassword
    }
  })
}

export const loginAPI = ({ username, password }) => {
  return request({
    url: '/api/login',
    method: 'POST',
    data: {
      username,
      password
    }
  })
}

export const getUserInfoAPI = () => {
  return request({
    url: '/my/userinfo'
  })
}

export const getMenuListAPI = () => {
  return request({
    url: '/my/menus'
  })
}

export const updateUserInfoAPI = ({ id, username, nickname, email, user_pic }) => {
  return request({
    url: '/my/userinfo',
    method: 'PUT',
    data: {
      id,
      username,
      nickname,
      email,
      user_pic
    }
  })
}

export const updateUserAvatarAPI = (avatar) => {
  return request({
    url: 'my/update/avatar',
    method: 'PATCH',
    data: {
      avatar
    }
  })
}
